exports.firstParentFolder = (substitutions) => {
  const folderPath = substitutions.folderPath; // Get the path to the note's folder
  if (!folderPath) {
    return ''; // Return empty if no folder path (e.g., note is in vault root)
  }
  const pathSegments = folderPath.split('/'); // Split the path into parts
  return pathSegments[0]; // Return the first part (the first parent folder name)
};
